import React, {useEffect, useState} from 'react';
import './App.css';

const App = () => {
  const APP_ID = '90cc4722';
  const APP_KEY = 'cceff0819eb59c1c4b888e52890fe97c';

  //use backticks to insert variables into the request URL 
  const exampleRequest = `https://api.edamam.com/search?q=chicken&app_id=${APP_ID}&app_key=${APP_KEY}`;


  const [counter, setCounter] =  useState(0);

  useEffect(() => {
    console.log('effect has been run');
  })

  return(
    <div className='App'>
      <form className="search-form">
        <input className="search-bar" type="text"></input>
        <button  onClick = {() => setCounter(counter + 1)} className="search-button" type="submit">{counter}</button>
      </form>


    </div>
  );
}

export default App;
